#include <stdio.h>
#include <string.h>
#include "eos_types.h"
#include "eos_types.h"
#include "eos_stream.h"
#include "eos_parse.h"

txProcessingContext_t txProcessingCtx;
txProcessingContent_t txContent;
cx_sha256_t sha256;
cx_sha256_t sha256_arg;

static int hex_to_bytes(
                 const char *hex,
                 size_t hex_len,
                 uint8_t *buffer,
                 size_t buffer_len
                 ) {
    if (hex_len % 2 != 0) {
        return -1;
    }
    
    if (hex_len / 2 != buffer_len) {
        return -1;
    }
    
    const char *pos = hex;
    
    for (size_t count = 0; count < hex_len / 2; ++count) {
        sscanf(pos, "%2hhx", &buffer[count]);
        pos += 2;
    }
    return 0;
}

bool onActionReady(txProcessingContext_t *context) {
    printf("--------------- Confirm action #%d ---------------\n", context->currentActionIndex + 1);
    
    puts("Contract");
    puts(context->content->contract);
    puts("\n");
    
    puts("Action");
    puts(context->content->action);
    puts("\n");
    
    for (uint8_t i = 0; i < context->content->argumentCount; ++i) {
        printArgument(i, context);
        puts(context->content->arg.label);
        puts(context->content->arg.data);
        
        puts("\n");
    }
    
    return true;
}

int main(int argc, const char * argv[]) {
#define NEWACCOUNT 1
#ifdef VOTE
   const char tx[] = "";
#elif PROXY
    const char tx[] =    "";
    
#elif UPDATE_AUTH
    const char tx[] = "";
#elif DELETE_AUTH
    const char tx[] = "";
#elif ARBITRARY
    const char tx[] = "";
#elif NEWACCOUNT
    const char tx[] = "";
#else
    const char tx[] = "";
#endif
    
    uint8_t buffer[strlen(tx)/2];
    hex_to_bytes(tx, strlen(tx), buffer, sizeof(buffer));
    
    txContent.callback = onActionReady;
    
    initTxContext(&txProcessingCtx, &sha256, &sha256_arg, &txContent, 1);
    parseTx(&txProcessingCtx, buffer, sizeof(buffer));
    
    unsigned char digest[32] = {0};
    cx_hash(&sha256.header, CX_LAST, digest, 0, digest);
    printf("Digest: ");
    for(int i = 0; i < 32; i++) {
        printf("%x", digest[i]);
    }
    
    puts("");
    
    return 0;
}
